ddls.ddl:
CREATE FUNCTION sales_tax(real) RETURNS real AS '
DECLARE
    subtotal ALIAS FOR $1;
BEGIN
    RETURN subtotal * 0.06;
END;' LANGUAGE plpgsql

CREATE FUNCTION sales_tax(real, int) RETURNS int AS '
DECLARE
  subtotal ALIAS FOR $1;
  other ALIAS FOR $2;
BEGIN
    RETURN other;
END;' LANGUAGE plpgsql

CREATE FUNCTION sales_tax(subtotal real) RETURNS real 
BEGIN
    RETURN subtotal * 0.06;
END;

CREATE FUNCTION sales_tax(subtotal real, other int) RETURNS int 
BEGIN
    RETURN other;
END;
